import { ITask } from "../dashboard/Section";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import { EditTask } from "./EditTask";
import React, { ChangeEvent } from "react";

interface Props extends ITask {
  deleteTask: (id: number) => void;
  setShowEditModal: (showModal: boolean) => void;
  showEditModal: boolean;
  taskFormData: ITask;
  setTaskFormData: React.Dispatch<React.SetStateAction<ITask>>;
}
export const Task = ({
  title,
  id,
  description,
  category,
  deleteTask,
  showEditModal,
  setShowEditModal,
  taskFormData,
  setTaskFormData,
}: Props) => {
  const editTask = () => {};

  const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {
    const { name, value } = event.target;
    setTaskFormData({
      ...taskFormData,
      [name]: value,
    });
  };

  return (
    <div className="flex justify-between p-6 max-w-sm bg-white rounded-lg border border-gray-200 shadow-md hover:bg-gray-100 my-5 ">
      <p>{title}</p>
      <div>
        <button className="mr-5" onClick={() => setShowEditModal(true)}>
          <FontAwesomeIcon icon={faEdit} />
        </button>
        <button className="" onClick={() => deleteTask(id)}>
          <FontAwesomeIcon icon={faTrash} />
        </button>
      </div>
      {showEditModal && (
        <EditTask
          setShowEditModal={setShowEditModal}
          handleChange={handleChange}
          taskFormData={taskFormData}
          editTask={editTask}
        />
      )}
    </div>
  );
};
