import React, { useState } from "react";
import { Sections } from "../dashboard/Section";
import { FormData } from "./EditTask";

interface Props {
  formData: FormData;
  category: string;
  handleChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
}

export const EditTaskForm = ({ formData, handleChange, category }: Props) => {
  const sections: Sections[] = ["to do", "in progress", "review", "done"];
  const handleSelectChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedValue(event.target.value);
  };
  const [selectedValue, setSelectedValue] = useState(category);
  return (
    <div className="relative p-6 flex-auto">
      <form className="flex text-center flex-col">
        <label>Title</label>
        <input
          name="title"
          type="text"
          value={formData.title}
          placeholder="Title"
          onChange={handleChange}
          className="border border-gray-300 p-2 rounded-md"
        />

        <label>Description</label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Description"
          className="border border-gray-300 p-2 rounded-md"
        />
        <label>Category</label>
        <select
          className="border border-gray-300 p-2 rounded-md"
          onChange={handleSelectChange}
          defaultValue={category}
        >
          {sections.map((section: String, index) => {
            return (
              <option value={section} key={index}>
                {section}
              </option>
            );
          })}
        </select>
      </form>
    </div>
  );
};
