import { ChangeEvent, useState } from "react";
import { generateRandomNumber } from "../../utils/utils";
import { CreateTask } from "./CreateTask";
import { ITask, Sections } from "./Section";
import { Task } from "./Task";

interface IProps {
  tasks: ITask[];
  sectionName: Sections;
  setTasks: (task: any) => void;
}
export const Board = ({ tasks, sectionName, setTasks }: IProps) => {
  const [showModal, setShowModal] = useState(false);
  const initialState = {
    id: generateRandomNumber(),
    title: "",
    description: "",
    category: sectionName,
  };
  const [taskFormData, setTaskFormData] = useState(initialState);

  const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {
    const { name, value } = event.target;
    setTaskFormData({
      ...taskFormData,
      [name]: value,
    });
  };

  const createTask = () => {
    const newTask = {
      ...taskFormData,
      title: taskFormData.title,
      description: taskFormData.description,
    };
    setTasks((prevTasks: ITask[]) => [...prevTasks, newTask]);
    setTaskFormData(initialState);
    setShowModal(false);
  };
  const deleteTask = (id: number) => {
    const updatedTasks = tasks.filter((task) => task.id !== id);
    console.log(updatedTasks);
    console.log(id);
    setTasks(updatedTasks);
  };
  return (
    <div className=" bg-gray-200 w-full mt-10 mx-5 rounded-md">
      <div className="mx-5 my-2">
        <div className="flex justify-between">
          <h2 className="text-2xl">{sectionName}</h2>
          <button className="text-2xl" onClick={() => setShowModal(true)}>
            +
          </button>
        </div>

        {tasks.map((task) => {
          return (
            <Task
              key={task.id}
              id={task.id}
              description={task.description}
              category={task.category}
              title={task.title}
              deleteTask={deleteTask}
            />
          );
        })}
      </div>
      {showModal && (
        <CreateTask
          setShowModal={setShowModal}
          handleChange={handleChange}
          taskFormData={taskFormData}
          createTask={createTask}
        />
      )}
    </div>
  );
};
