import React from "react";
import { Sections } from "../dashboard/Section";
import { FormData } from "./EditTask";

interface Props {
  formData: FormData;
  handleChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
}

export const EditTaskForm = ({ formData, handleChange }: Props) => {
  const sections: Sections[] = ["to do", "in progress", "review", "done"];

  return (
    <div className="relative p-6 flex-auto">
      <form className="flex text-center flex-col">
        <label>Title</label>
        <input
          name="title"
          type="text"
          value={formData.title}
          placeholder="Title"
          onChange={handleChange}
          className="border border-gray-300 p-2 rounded-md"
        />

        <label>Description</label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Description"
          className="border border-gray-300 p-2 rounded-md"
        />
        <label>Category</label>
        <select
          className="border border-gray-300 p-2 rounded-md"
          onChange={handleSelectChange}
